mapper.select:

select id, title, logo 
from blog 
where id=#{id }

choose.when:

update blog 
set title=#{title }, logo=#{logo }
where id=#{id }

choose.otherwise:

update blog 
set title=#{title }
where id=#{id }


select no, title, contents, category_no 
from post 
where category_no=#{category_no } and no=#{no }
